mapper.select:
select * from Board
where MEMBER_ID = #{member_id}
order by Board_Num ASC

mapper.update:
update BOARD set 
TITLE = #{title},
B_CONTENT = #{b_Content},
MEET_TIME = REPLACE(#{meet_Time},':',''),
NUM_PEOPLE = #{num_People}
WHERE BOARD_NUM = #{board_Num}

mapper.select:
	select board.* , na_member.m_profile
from BOARD
JOIN NA_MEMBER ON BOARD.#{member_ID} = NA_MEMBER#{member_ID};
